mapper.select:
SELECT LANTOUR_NO, MEMBER_NO, LANTOUR_TITLE, LANTOUR_CONTENT, LANTOUR_REGDATE, LANTOUR_RTC, LANTOUR_ADDR, LANTOUR_IMAGE, LANTOUR_MAXCOUNT, LANTOUR_PRICE, LANTOUR_DATE
FROM LANTOUR
WHERE LANTOUR_NO = #{lantour_no}

mapper.update:
UPDATE LANTOUR
SET LANTOUR_TITLE =#{lantour_title}, LANTOUR_CONTENT = #{lantour_content}, LANTOUR_RTC = #{lantour_rtc}, LANTOUR_ADDR = #{lantour_addr}, LANTOUR_IMAGE = #{lantour_image}, LANTOUR_MAXCOUNT = #{lantour_maxcount}, LANTOUR_PRICE = #{lantour_price}, LANTOUR_DATE = #{lantour_date}
WHERE LANTOUR_NO = #{lantour_no}

UPDATE LANTOUR
SET LANTOUR_RTC = #{lantour_rtc} 
WHERE LANTOUR_NO = #{lantour_no}
