mapper.update:
UPDATE MEMBER
SET MEMBER_NAME =
#{member_name}, MEMBER_PASSWORD = #{member_password},
MEMBER_EMAIL =
#{member_email}, MEMBER_PHONE = #{member_phone}
WHERE MEMBER_NO = #{member_no}

UPDATE MEMBER
SET
MEMBER_SECESSION = 'Y' WHERE MEMBER_NO = #{member_no}

UPDATE MEMBER
SET
MEMBER_GRADE = #{member_grade}
WHERE MEMBER_NO = #{member_no}

mapper.select:
SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_NAME =
#{member_name}
AND MEMBER_EMAIL = #{member_email}

SELECT MEMBER_NO, MEMBER_ID,
MEMBER_NAME, MEMBER_PASSWORD, MEMBER_GRADE,
MEMBER_AGE, MEMBER_GENDER,
MEMBER_EMAIL, MEMBER_PHONE,
MEMBER_SECESSION
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
AND MEMBER_NAME = #{member_name}
AND MEMBER_EMAIL = #{member_email}

mapper.update:
UPDATE MEMBER
SET
MEMBER_PASSWORD = #{member_password}
WHERE MEMBER_ID = #{member_id}
